mapper.insert:


	select goods_seq.nextval from dual


insert into vam_book(
	bookId
	, bookName
	, authorId
	, publeYear
	, publisher
	, cateCode
	, bookPrice
	, bookStock
	, bookDiscount
	, bookIntro
	, bookContents
	)
	values (
	#{bookId}
	, #{bookName}
	, #{authorId}
	, #{publeYear}
	, #{publisher}
	, #{cateCode}
	, #{bookPrice}
	, #{bookStock}
	, #{bookDiscount}
	, #{bookIntro}
	, #{bookContents}

	)

mapper.select:


select * from(
    select /*+INDEX_DESC(vam_book SYS_C0011315)*/ rownum as rn, bookId, bookName,
        (select authorName from vam_author where vam_book.authorId = vam_author.authorId) authorName,
        (select cateName from vam_bcate where vam_book.cateCode = vam_bcate.cateCode) cateName,bookStock,regDate
    from vam_book 
    where 



	    bookName like '%' || #{keyword} || '%' and


    rownum   <=  #{pageNum} * #{amount} 
    )
where rn  >  (#{pageNum} -1) * #{amount}

mapper.update:

  		update vam_book 
	  		   set bookName = #{bookName}
	  		     , authorId = #{authorId}
	  			 , publeYear = #{publeYear}
	  			 , publisher = #{publisher}
	  			 , cateCode = #{cateCode}
	  			 , bookPrice = #{bookPrice}
	  			 , bookStock = #{bookStock}
	  			 , bookDiscount = #{bookDiscount}
	  			 , bookIntro = #{bookIntro}
	  			 , bookContents = #{bookContents}
	  			 , updateDate = sysdate
 			where bookId = ${bookId}

mapper.insert:




	select last_number
	  from user_sequences
	 where sequence_name = 'GOODS_SEQ'




