mapper.select:
select * from (
	select rownum rn, orderId, memberId, orderState, orderDate
	  from vam_order
	 where rownum  <=  #{pageNum} * #{amount}


		and memberId like '%'||#{keyword}||'%'

		order by orderDate desc
	)
	 where rn  >  (#{pageNum} -1) * #{amount}
